_PrintNum:: ; c4c7
; Print c digits of the b-byte value at hl.
; Allows 2 to 7 digits. For 1-digit numbers, add
; the value to char "0" instead of calling PrintNum.
; Some extra flags can be given in bits 5-7 of b.

	push bc

	bit 5, b
	jr z, .main
	bit 7, b
	jr nz, .bit_7
	bit 6, b
	jr z, .main

.bit_7
	ld a, $f0
	ld [hli], a
	res 5, b

.main
	xor a
	ld [$ffb3], a
	ld [$ffb4], a
	ld [$ffb5], a
	ld a, b
	and $f
	cp 1
	jr z, .byte
	cp 2
	jr z, .word

.long
	ld a, [de]
	ld [$ffb4], a
	inc de
	ld a, [de]
	ld [$ffb5], a
	inc de
	ld a, [de]
	ld [$ffb6], a
	jr .start

.word
	ld a, [de]
	ld [$ffb5], a
	inc de
	ld a, [de]
	ld [$ffb6], a
	jr .start

.byte
	ld a, [de]
	ld [$ffb6], a

.start
	push de

	ld d, b
	ld a, c
	swap a
	and $f
	ld e, a
	ld a, c
	and $f
	ld b, a
	ld c, 0
	cp 2
	jr z, .two
	cp 3
	jr z, .three
	cp 4
	jr z, .four
	cp 5
	jr z, .five
	cp 6
	jr z, .six

.seven
	ld a, 1000000 / $10000 % $100
	ld [$ffb7], a
	ld a, 1000000 / $100 % $100
	ld [$ffb8], a
	ld a, 1000000 % $100
	ld [$ffb9], a
	call .PrintDigit
	call .AdvancePointer

.six
	ld a, 100000 / $10000 % $100
	ld [$ffb7], a
	ld a, 100000 / $100 % $100
	ld [$ffb8], a
	ld a, 100000 % $100
	ld [$ffb9], a
	call .PrintDigit
	call .AdvancePointer

.five
	xor a
	ld [$ffb7], a
	ld a, 10000 / $100
	ld [$ffb8], a
	ld a, 10000 % $100
	ld [$ffb9], a
	call .PrintDigit
	call .AdvancePointer

.four
	xor a
