	ld a, c 

; Skip any gaps.
	cp ITEM_C3 - (TM_01 - 1)
	jr c, .done
	cp ITEM_DC - (TM_01 - 1) - 1
	jr c, .skip_one

.skip_two
	inc a
.skip_one
	inc a
.done
	add TM_01
	dec a
	ld c, a
	ret
